document.addEventListener("DOMContentLoaded",function(){const hamburgerBtn=document.querySelector(".hamburger-btn");const navOverlay=document.getElementById("navOverlay");const navOverlayClose=document.getElementById("navOverlayClose");const navOverlayLinks=document.querySelectorAll(".nav-overlay__link");const menuSection=document.getElementById("menuSections");const footer=document.querySelector(".choco-footer");const cartButton=document.querySelector(".cart-btn");const menuBtn=document.querySelector(".menu-btn");const contactBtn=document.querySelector(".contact-btn");if(!hamburgerBtn||!navOverlay||!navOverlayClose){return}function openNavOverlay(){navOverlay.style.display="flex";setTimeout(()=>navOverlay.classList.add("open"),10);navOverlay.setAttribute("aria-hidden","false");document.body.style.overflow="hidden"}function closeNavOverlay(){navOverlay.classList.remove("open");navOverlay.setAttribute("aria-hidden","true");setTimeout(()=>{navOverlay.style.display="none";document.body.style.overflow=""},350)}hamburgerBtn.addEventListener("click",function(e){e.preventDefault();if(navOverlay.classList.contains("open")){closeNavOverlay()}else{openNavOverlay()}});navOverlayClose.addEventListener("click",closeNavOverlay);document.addEventListener("keydown",function(e){if(navOverlay.classList.contains("open")&&(e.key==="Escape"||e.key==="Esc")){closeNavOverlay()}});navOverlayLinks.forEach(link=>{link.addEventListener("click",function(e){const href=link.getAttribute("href");const nav=link.getAttribute("data-nav");if(href&&href!=="#"&&!nav){return}e.preventDefault();closeNavOverlay();if(nav==="menu"&&menuSection){menuSection.scrollIntoView({behavior:"smooth",block:"start"})}else if(nav==="contact"){if(window.location.pathname.includes("index.html")||window.location.pathname.endsWith("/")||window.location.pathname===""){if(footer){footer.scrollIntoView({behavior:"smooth",block:"start"})}}else{window.location.href="/index.html#contact"}}else if(nav==="cart"){if(window.openCartModal){window.openCartModal()}else{const cartModal=document.getElementById("cart-modal");if(cartModal){cartModal.style.display="flex";setTimeout(()=>cartModal.classList.add("active"),10);cartModal.setAttribute("aria-hidden","false");document.body.style.overflow="hidden"}}}})});if(menuBtn){menuBtn.addEventListener("click",function(e){const href=menuBtn.getAttribute("href");if(!href||href==="#"){e.preventDefault();menuBtn.style.transform="scale(0.98)";setTimeout(()=>{menuBtn.style.transform=""},150);if(menuSection){menuSection.scrollIntoView({behavior:"smooth",block:"start"})}}})}if(contactBtn){contactBtn.addEventListener("click",function(e){const href=contactBtn.getAttribute("href");if(!href||href==="#"){e.preventDefault();contactBtn.style.transform="scale(0.98)";setTimeout(()=>{contactBtn.style.transform=""},150);if(footer){footer.scrollIntoView({behavior:"smooth",block:"start"})}}})}});